INSERT INTO `ot_goods` VALUES ('34', '2013-05-22 12:18:47', '2013-06-17 16:43:40', 'goods', '55', '0', '哈密瓜', '/500g', '3.60', '2203', '2000g-3000g', '201305221219432227.jpg', 'a:1:{i:0;s:22:\"201305221219514278.jpg\";}', '', '2077', '<p>\r\n	<img src=\"/include/modules/kindeditor/php/../../../../upfiles/attached/image/20130522/20130522042023_85824.jpg\" width=\"500\" height=\"350\" title=\"www.buyfer.com百惠臻品\" align=\"left\" alt=\"www.buyfer.com百惠臻品\" /> \r\n</p>\r\n<p>\r\n	<span style=\"color:#E53333;font-family:宋体, \'Arial Narrow\', HELVETICA;line-height:24px;font-size:18px;\"><span style=\"color:#E53333;font-family:\'Microsoft YaHei\';font-size:18px;line-height:20px;background-color:#FFFFFF;\"><strong>商品介绍：</strong></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#009900;font-family:宋体, \'Arial Narrow\', HELVETICA;line-height:24px;font-size:18px;\"><span style=\"font-family:\'Microsoft YaHei\';line-height:20px;background-color:#FFFFFF;\"><strong><span style=\"line-height:2;\">食</span><span style=\"line-height:2;\"></span><span style=\"line-height:2;\">用哈密瓜对人体造血机能有</span></strong></span></span><strong><span style=\"font-size:18px;color:#009900;line-height:2;\">显著的促进作用，可以用作</span></strong><strong><span style=\"font-size:18px;color:#009900;line-height:2;\">为贫血的食疗之品，哈密香</span></strong><strong><span style=\"font-size:18px;color:#009900;line-height:2;\">甜可口，果肉细腻，而且肉</span></strong><strong><span style=\"font-size:18px;color:#009900;line-height:2;\">愈靠近种子处，甜度越高愈</span></strong><strong><span style=\"color:#009900;font-size:18px;line-height:2;\">靠近果皮越硬，因此皮最削</span></strong><strong><span style=\"color:#009900;font-size:18px;line-height:2;\">厚一点，吃起来更美味，密</span></strong><strong><span style=\"color:#009900;font-size:18px;line-height:2;\">瓜不但好吃，而且营养富，</span></strong><strong><span style=\"color:#009900;font-size:18px;line-height:2;\">药用价值高。</span></strong> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<b><span style=\"font-size:18px;color:#E53333;\">注意事项：</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"background-color:white;\">\r\n	<span style=\"font-size:16px;color:#009900;\"><strong>搬动哈密瓜应轻拿轻放，不要碰伤瓜皮</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>。受伤后的瓜很容易变质腐烂</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>，不能储藏</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>。</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"background-color:white;\">\r\n	<span style=\"font-size:16px;color:#009900;\"><strong>哈密瓜性凉，不宜吃得过多，以免引起腹泻</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>。</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"background-color:white;\">\r\n	<span style=\"font-size:16px;color:#009900;\"><strong>一般人群均可食用。特别适宜于肾病、胃病、咳嗽痰喘</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>、贫血和便秘患者。</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"background-color:white;\">\r\n	<span style=\"font-size:16px;color:#009900;\"><strong>患有脚气</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>病、黄疸</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>、腹胀</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>、便溏</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>、寒性咳喘</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>以及产后、病后的人不宜多食。</strong></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"/include/modules/kindeditor/php/../../../../upfiles/attached/image/20130522/20130522042023_26067.jpg\" width=\"700\" height=\"488\" title=\"www.buyfer.com百惠臻品\" alt=\"www.buyfer.com百惠臻品\" /> \r\n</p>\r\n<p>\r\n	<span><span style=\"line-height:24px;font-size:18px;color:#E53333;\"><b>挑选窍门：</b></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;color:#009900;\"><strong>哈密瓜多为椭圆形或橄榄形。它的颜色为果绿色带网纹的， 金黄色的，花青色的等等。 用鼻子</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>去闻瓜，一般有香味</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>的，成熟度适中。没有香味</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>或香味淡薄的，是成熟度较差的，可以放些时候再吃。挑瓜时可以用手摸一摸，如果瓜身坚实微软，成熟度就比较适中。如果太硬则不太熟，太软就是成熟过度。瓜瓤</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>为浅绿色的，吃时发脆，如是金黄色的，吃上去发粘，白色柔软多汁。总之，哈密瓜的种类很多，希望您挑选时注意这几点.</strong></span> \r\n</p>\r\n<p>\r\n	<img src=\"/include/modules/kindeditor/php/../../../../upfiles/attached/image/20130522/20130522042023_86904.jpg\" width=\"600\" height=\"401\" title=\"www.buyfer.com百惠臻品\" alt=\"www.buyfer.com百惠臻品\" /><img src=\"/include/modules/kindeditor/php/../../../../upfiles/attached/image/20130522/20130522042023_78829.jpg\" width=\"596\" height=\"566\" title=\"www.buyfer.com百惠臻品\" alt=\"www.buyfer.com百惠臻品\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<div style=\"border:none;padding:0cm 0cm 5.0pt 0cm;background:white;\">\r\n	<p class=\"MsoNormal\" align=\"left\" style=\"background-color:white;\">\r\n		<br />\r\n	</p>\r\n</div>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"text-indent:22.5pt;background-color:white;\">\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"background-color:white;\">\r\n	<b><span style=\"font-size:18px;color:#E53333;\"><strong>食疗作用：</strong></span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"background-color:white;\">\r\n	<span style=\"font-size:16px;color:#009900;\"><strong>哈密瓜，性寒味甘，含蛋白质、膳食纤维、胡萝卜素</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>、果胶</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>、糖类</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>、维生素A、维生素B、维生素C、磷、钠、钾等。哈密瓜果肉</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>有利小便、止咳</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>、除烦热、防暑气等作用，可治发烧</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>、中暑、口渴</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>、尿路感染</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>、口鼻生疮等症状并且有清凉消暑</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>，除烦热，生津止渴的作用，是夏季解暑的佳品。</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>食用哈密瓜对人体造血机能有显著的促进作用，可以用来作为贫血的食疗之品。如果常感到身 心疲倦、心神焦躁不安</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>或是口臭</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>，食用哈密瓜都能有所改善。</strong></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '', '39', '0', '1', '1', '1', '哈密瓜|瓜', '哈密瓜');
