INSERT INTO `ot_goods` VALUES ('43', '2013-05-22 14:47:41', '2013-06-14 23:37:52', 'goods', '49', '0', '紫甘蓝', '', '0.00', '1109', '700-850g', '201305241332102240.jpg', 'a:1:{i:0;s:22:\"201305241332205915.jpg\";}', '38,145', '2117', '<p>\r\n	<strong><span style=\"font-family:NSimSun;color:#E53333;line-height:2;\"><span style=\"font-family:NSimSun;font-size:18px;line-height:2;\">商品详情</span></span></strong><strong><span style=\"font-family:NSimSun;color:#E53333;line-height:2;\"><span style=\"font-family:NSimSun;font-size:18px;line-height:2;\">&nbsp; &nbsp; &nbsp; &nbsp;</span></span></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-family:\'Microsoft YaHei\';color:#006600;font-size:16px;\"><span style=\"font-size:14px;font-family:NSimSun;line-height:2;color:#009900;\">品&nbsp;名：紫甘蓝&nbsp;</span><br />\r\n<span style=\"font-size:14px;font-family:NSimSun;line-height:2;color:#009900;\">功&nbsp;效：强身健体、维护皮肤健康&nbsp;燃烧脂肪、减肥</span><br />\r\n<br />\r\n</span></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-family:NSimSun;color:#E53333;line-height:2;\"><span style=\"font-family:NSimSun;color:#006600;font-size:16px;line-height:2;\"><span style=\"font-size:18px;font-family:NSimSun;line-height:2;\"><span style=\"color:#E53333;font-family:NSimSun;line-height:2;\">商品特色</span></span></span></span></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-family:微软雅黑;color:#E53333;\"><span style=\"font-family:\'Microsoft YaHei\';color:#006600;font-size:16px;\"><img src=\"http://www.justeasy.com.cn/img/upload/20130306/20130306094122493.jpg\" width=\"429\" height=\"285\" align=\"right\" alt=\"\" /></span></span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;color:#009900;font-size:14px;line-height:2;\"><span style=\"font-family:NSimSun;color:#009900;font-size:14px;line-height:2;\"><strong>紫甘蓝又称红甘蓝、赤甘蓝，十字花科、芸苔属甘蓝种中的一个变种，紫甘蓝以紫红色的叶球供食用，营养丰富，尤以丰富的维生素C、较多的维生素E和维生素B族，以及丰富的花青素甙和纤维素等，备受人们的欢迎，紫甘蓝具有重要的医学保健作用，是一种天然的防癌药物。甘蓝中含有丰富的维生素C，维生素E，维生素U，胡&nbsp;萝卜素，钙、锰，钼以及纤维素</strong></span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-family:NSimSun;color:#E53333;font-size:18px;line-height:2;\"><span style=\"font-size:18px;color:#E53333;font-family:NSimSun;line-height:2;\">适合人群</span></span></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-family:NSimSun;color:#009900;font-size:16px;line-height:2;\"><span style=\"font-size:14px;font-family:NSimSun;line-height:2;color:#009900;\">一般人群均可食用</span></span></strong> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;color:#E53333;font-size:18px;line-height:2;\"><span style=\"font-family:NSimSun;color:#006600;font-size:18px;line-height:2;\"><strong><span style=\"font-size:18px;font-family:NSimSun;line-height:2;\"><span style=\"font-size:18px;font-family:NSimSun;line-height:2;\"><span style=\"color:#E53333;font-size:18px;font-family:NSimSun;line-height:2;\">小贴士</span></span></span></strong></span></span> \r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#009900;font-family:NSimSun;font-size:14px;line-height:2;\">紫甘蓝因含丰富的色素，是拌色拉或西餐配色的好原料。在炒或煮紫甘蓝时，要保持其艳丽的紫红色，在操作前，必须加少许白醋，否则，经加热后就会变成黑紫色，影响美观</span></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#006600;\"><br />\r\n</span></strong> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:微软雅黑;color:#E53333;\"><span style=\"font-family:\'Microsoft YaHei\';color:#006600;font-size:16px;\"><strong><span><span><span style=\"color:#E53333;\"><span style=\"font-size:18px;\">美味连连看</span></span></span></span></strong></span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:微软雅黑;color:#E53333;\"><span style=\"font-family:\'Microsoft YaHei\';color:#006600;font-size:16px;\"><strong><span><span><span style=\"color:#E53333;\"><span style=\"font-size:18px;\"><img border=\"0\" alt=\"\" src=\"http://www.justeasy.com.cn/img/upload/20130306/20130306094904239.jpg\" style=\"height:auto;width:auto;\" /></span></span></span></span></strong></span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:微软雅黑;color:#E53333;font-size:18px;\"><span style=\"font-family:\'Microsoft YaHei\';color:#E53333;font-size:18px;\"><strong><span style=\"font-size:18px;color:#E53333;\"><span style=\"font-size:18px;color:#E53333;\"><span style=\"color:#E53333;font-size:18px;\"><span style=\"color:#E53333;font-size:18px;\">蔬菜沙拉</span></span></span></span></strong></span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:微软雅黑;color:#009900;\"><span style=\"font-family:\'Microsoft YaHei\';color:#009900;font-size:16px;\"><strong>用 料：</strong></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:微软雅黑;color:#E53333;\"><span style=\"font-family:\'Microsoft YaHei\';color:#006600;font-size:16px;\"><strong><span style=\"font-size:14px;color:#009900;\">紫甘蓝 &nbsp; &nbsp; 生菜 &nbsp; &nbsp;</span><span style=\"font-size:14px;\"><span style=\"color:#009900;\">西兰花 &nbsp; &nbsp; &nbsp;小番茄干 &nbsp; &nbsp;</span><strong><span style=\"font-size:14px;color:#009900;\">沙拉酱 &nbsp; &nbsp; &nbsp;橄榄油 &nbsp;</span></strong><span style=\"color:#009900;\"> &nbsp;&nbsp;</span><strong><span style=\"font-size:14px;color:#009900;\">白醋&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 糖&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 盐</span><span style=\"color:#009900;\">&nbsp;&nbsp;</span></strong></span><br />\r\n<br />\r\n</strong></span></span><strong><span style=\"color:#E53333;font-size:18px;\">蔬菜沙拉的做法</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:微软雅黑;color:#E53333;\"><span style=\"font-family:\'Microsoft YaHei\';color:#006600;font-size:16px;\"><strong>&nbsp;&nbsp;&nbsp;<br />\r\n<span style=\"font-size:14px;color:#009900;\">1.&nbsp;先取下两片紫甘蓝叶子垫在盘底&nbsp;</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#009900;\">2.&nbsp;将紫甘蓝中间的硬梗去掉，不然会影响口感&nbsp;</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#009900;\">3.&nbsp;生菜用手随意撕成小块，在橄榄切成细丝&nbsp;</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#009900;\">4.&nbsp;西兰花洗净后切成小块，放入沸水中焯熟，焯时可滴两滴橄榄油，保持亮泽&nbsp;</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#009900;\">5.&nbsp;把所有原料倒入盘中混合，然后在放入调料即可</span></strong></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#009900;\">&nbsp;</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:微软雅黑;color:#E53333;font-size:18px;\"><span style=\"font-family:\'Microsoft YaHei\';color:#E53333;font-size:18px;\"><strong><span style=\"font-size:18px;color:#E53333;\"><span style=\"font-size:18px;color:#E53333;\"><span style=\"color:#E53333;font-size:18px;\"><span style=\"color:#E53333;font-size:18px;\">装盘用餐哦</span></span></span></span></strong></span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:微软雅黑;color:#E53333;\"><span style=\"font-family:\'Microsoft YaHei\';color:#006600;font-size:16px;\"><strong><img border=\"0\" alt=\"\" src=\"http://www.justeasy.com.cn/img/upload/20130306/20130306095427206.jpg\" style=\"height:auto;width:auto;\" /></strong></span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:微软雅黑;color:#E53333;font-size:18px;\"><span style=\"font-family:\'Microsoft YaHei\';color:#E53333;font-size:18px;\"><strong><span style=\"color:#E53333;font-size:18px;\"><span style=\"color:#E53333;font-size:18px;\"><span style=\"color:#E53333;font-size:18px;\"><span style=\"color:#E53333;font-size:18px;\">紫甘蓝拌豆腐丝</span></span></span></span></strong></span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:微软雅黑;color:#E53333;\"><span style=\"font-family:\'Microsoft YaHei\';color:#006600;font-size:16px;\"><strong><span style=\"color:#009900;\">用&nbsp;料：&nbsp;</span><span style=\"color:#009900;font-size:14px;\"><span style=\"color:#009900;\">紫甘蓝&nbsp;150g&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;豆腐皮&nbsp;50g &nbsp; &nbsp;</span><strong><span style=\"color:#009900;font-size:14px;\"><span style=\"color:#009900;\">蒜末 &nbsp; </span><span style=\"color:#009900;\">&nbsp; 葱末 &nbsp;</span></span></strong><span style=\"color:#009900;\">&nbsp;</span><strong><span style=\"color:#009900;font-size:14px;\">干辣椒 &nbsp; &nbsp;花椒 &nbsp; &nbsp;</span></strong><span style=\"color:#006600;\"></span><strong><span style=\"color:#009900;font-size:14px;\"><span style=\"color:#009900;\">白醋 &nbsp;</span><span style=\"color:#009900;\">&nbsp; 白糖 &nbsp;&nbsp;</span></span></strong><span style=\"color:#009900;\">&nbsp;</span><strong><span style=\"color:#009900;font-size:14px;\">盐 &nbsp; &nbsp;&nbsp;油适量&nbsp;</span></strong></span><br />\r\n</strong></span></span> \r\n</p>\r\n<br />\r\n<p>\r\n	<span style=\"font-family:微软雅黑;color:#E53333;\"><span style=\"font-family:\'Microsoft YaHei\';color:#006600;font-size:16px;\"><strong><span style=\"color:#E53333;font-size:18px;\">紫甘蓝拌豆腐丝的做法</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#009900;\">1.&nbsp;紫甘蓝一叶一叶掰开，放到水里泡半小时&nbsp;</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#009900;\">2.&nbsp;豆腐皮洗净，切成丝&nbsp;</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#009900;\">3.&nbsp;紫甘蓝洗净切细丝，切的越细越好（拌起来容易入味）&nbsp;</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#009900;\">4.&nbsp;豆腐丝和紫甘蓝收到一起&nbsp;</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#009900;\">5.&nbsp;开火，稍有，把花椒炸香捞出&nbsp;</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#009900;\">6.&nbsp;葱末和干辣椒放到油里，炸香&nbsp;</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#009900;\">7.&nbsp;炸好的葱油凉温，泼在豆腐丝上&nbsp;</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#009900;\">8.&nbsp;放蒜末、白醋、白糖和盐搅拌均匀即可</span></strong></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#009900;\">&nbsp;</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:微软雅黑;color:#E53333;\"><span style=\"font-family:\'Microsoft YaHei\';color:#006600;font-size:16px;\"><strong><span style=\"color:#E53333;font-size:18px;\">小贴士</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#009900;\">1、此凉拌菜非常适合脑力劳动者经常食用。&nbsp;</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#009900;\">2、紫甘蓝最好生吃，生吃的保健作用最好，它的活性最强。&nbsp;</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#009900;\">3、切紫甘蓝丝的时候，切得越细越好，拌起来更容易入味儿。&nbsp;</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#009900;\">4、炸葱花和辣椒的时候，要注意火候不要太大，否则会炸糊。&nbsp;</span><br />\r\n<br />\r\n<span style=\"font-size:14px;color:#009900;\">5、紫甘蓝性味有点偏寒，所以脾胃虚寒，消化不良的人不适合.</span></strong></span></span> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:14px;color:#009900;\"><span style=\"color:#009900;font-family:Arial, Verdana, Tahoma, 宋体;background-color:#FFFFFF;\">制作工艺</span></span></strong> \r\n</p>', '', '35', '0', '1', '0', '0', '紫甘蓝|百惠臻品', '紫甘蓝  百惠臻品源自大自然的味道。');
INSERT INTO `ot_goods` VALUES ('47', '2013-05-22 16:00:30', '2013-06-15 15:25:12', 'goods', '58', '0', '菠萝蜜', '/500g', '0.00', '2407', '250g-500g', '201305221601301716.jpg', 'a:2:{i:0;s:22:\"201305221601397347.jpg\";i:1;s:22:\"201305221601549745.jpg\";}', '', '2137', '<p>\r\n	<img src=\"/include/modules/kindeditor/php/../../../../upfiles/attached/image/20130522/20130522080212_97813.jpg\" width=\"600\" height=\"406\" title=\"www.buyfer.com百惠臻品\" alt=\"www.buyfer.com百惠臻品\" /> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"color:#E53333;font-size:18px;line-height:1.5;\"><strong>商品介绍：</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"color:#009900;font-size:18px;line-height:1.5;\"><strong>果实为长椭圆形，棕绿色，一般重8千克至10千克，最大可达15千克至20千克，为水果中果实最大之王。菠萝蜜果的果实浅黄色，品种主要分为湿包和干包两类，干包菠萝蜜果皮坚硬，肉瓤肥厚、汁少、味甜、</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"color:#009900;font-size:18px;line-height:1.5;\"><strong>香气特殊而浓；湿包菠萝蜜果汁多、柔软甜滑，鲜食味甘美，香气中等；成熟时，皮黄绿或黄褐色，外皮有六角形瘤突起，坚硬有软刺；肉淡黄白色，味甘甜多汁，故名菠萝蜜。菠萝蜜果有止渴、通乳、补中益气功效，品质以干包菠萝蜜果为佳，在雷州半岛地区多为零星种植于庭院中，菠萝蜜包大味美，而闻名遐迩。</strong></span> \r\n</p>\r\n<span></span> \r\n<h3 class=\"headline-2\" style=\"font-size:16px;font-family:Arial;background-color:#FFFFFF;\">\r\n	<span class=\"headline-content\" style=\"color:#E53333;font-size:18px;\"><strong><br />\r\n</strong></span> \r\n</h3>\r\n<h3 class=\"headline-2\" style=\"font-size:16px;font-family:Arial;background-color:#FFFFFF;\">\r\n	<span class=\"headline-content\" style=\"color:#E53333;font-size:18px;line-height:1.5;\"><strong>食用价值：</strong></span> \r\n</h3>\r\n<h3 class=\"headline-2\" style=\"font-size:16px;font-family:Arial;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:arial, 宋体, sans-serif;color:#009900;font-size:18px;line-height:1.5;\"><strong>绿色未成熟的果实可作蔬菜食用。成熟时的可食部分每100克含碳水化合物24</strong></span><strong><span style=\"color:#009900;line-height:1.5;\">.9克，钙25毫克、磷5毫克、铁7毫克等。将其炒熟食用，味美如栗，有止渴、通乳、补中益气功效。</span></strong> \r\n</h3>\r\n<span style=\"color:#E53333;font-size:18px;\"></span> \r\n<p>\r\n	<strong><span style=\"font-family:\'Microsoft YaHei\';color:#009900;font-size:16px;\"><span style=\"color:#009900;\"><span style=\"color:#009900;\"><span style=\"color:#E53333;font-size:18px;\"><br />\r\n</span></span></span></span></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-family:\'Microsoft YaHei\';color:#009900;font-size:16px;\"><span style=\"color:#009900;\"><span style=\"color:#009900;\"><span style=\"color:#E53333;font-size:18px;\"><br />\r\n</span></span></span></span></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-family:\'Microsoft YaHei\';color:#009900;font-size:16px;\"><span style=\"color:#009900;\"><span style=\"color:#009900;\"><span style=\"color:#E53333;font-size:18px;\"><br />\r\n</span></span></span></span></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-family:\'Microsoft YaHei\';color:#009900;font-size:16px;\"><span style=\"color:#009900;\"><span style=\"color:#009900;\"><span style=\"color:#E53333;font-size:18px;line-height:1.5;\">注&nbsp; 意：</span><span style=\"line-height:1.5;\">该品食用可能引起一些人的过敏反应，如皮肤潮红、发疹、瘙痒等。一般认为和某些个体对所含波萝蛋白酶过敏有关。民间经验，食用前先以盐水浸泡，可比较有效的免过敏。</span></span></span></span></strong> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"/include/modules/kindeditor/php/../../../../upfiles/attached/image/20130522/20130522080212_66513.jpg\" width=\"600\" height=\"421\" title=\"www.buyfer.com\" alt=\"www.buyfer.com\" /> \r\n</p>', '', '14', '0', '', '0', '0', '菠萝蜜', '菠萝蜜');
