INSERT INTO `ot_goods` VALUES ('49', '2013-05-22 16:09:12', '2013-06-15 15:24:30', 'goods', '56', '0', '红提', '/500g', '12.50', '2502', '250g-500g', '201305231726245814.jpg', 'a:1:{i:0;s:22:\"201305231726357743.jpg\";}', '', '2147', '<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"font-size:18px;color:#009900;\"><strong><img src=\"/upfiles/attached/image/20130523/20130523092654_24136.jpg\" width=\"350\" height=\"350\" title=\"www.buyfer.com百惠臻品\" align=\"left\" alt=\"www.buyfer.com百惠臻品\" /><span style=\"color:#E53333;\">商品介绍：</span></strong></span> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\">\r\n	<span style=\"font-size:18px;color:#009900;line-height:1.5;\"><strong>&nbsp; &nbsp;红提营养十分丰富，它含有17%以上的葡萄糖和果糖</strong></span><span style=\"font-size:18px;color:#009900;line-height:1.5;\"><strong>，0.5%-1.5%的苹果酸</strong></span><span style=\"font-size:18px;color:#009900;line-height:1.5;\"><strong>、酒石酸、柠檬酸</strong></span><span style=\"font-size:18px;color:#009900;line-height:1.5;\"><strong>等。含0.15%-0.9%的蛋白质</strong></span><span style=\"font-size:18px;color:#009900;line-height:1.5;\"><strong>和丰富的钾、钙、钠、锰等人体所必需的微量元素</strong></span><span style=\"font-size:18px;color:#009900;line-height:1.5;\"><strong>，含有多种维生素和氨基酸</strong></span><span style=\"font-size:18px;color:#009900;line-height:1.5;\"><strong>。</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;color:#009900;line-height:1.5;\"><strong>&nbsp; &nbsp;一升葡萄汁相当于1.7升牛奶，650克牛肉，1000克鱼，500克面包，3-5个鸡蛋，1.2千克马铃薯</strong></span><span style=\"font-size:18px;color:#009900;line-height:1.5;\"><strong>，3.5千克番茄</strong></span><span style=\"font-size:18px;color:#009900;line-height:1.5;\"><strong>，1.5千克苹果、梨、桃产生的热量</strong></span><span style=\"font-size:18px;color:#009900;line-height:1.5;\"><strong>，对人体具有很多保健作用，饭前和酒后服用可起到解酒的作用，经常食用可清除体内自由基，阻止血小板凝聚，</strong></span><span style=\"font-size:18px;color:#009900;line-height:1.5;\"><strong>防止人身体低密度脂蛋白</strong></span><span style=\"font-size:18px;color:#009900;line-height:1.5;\"><strong>氧化，抗肿瘤</strong></span><span style=\"font-size:18px;color:#009900;line-height:1.5;\"><strong>等作用。</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;color:#009900;\"><strong><span style=\"font-family:Arial, Verdana, Tahoma, 宋体;line-height:20px;background-color:#FFFFFF;font-weight:bold;color:#E53333;\"><br />\r\n</span></strong></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<strong><span style=\"font-family:Arial, Verdana, Tahoma, 宋体;line-height:20px;background-color:#FFFFFF;color:#E53333;\">饮食宜忌：</span></strong> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;color:#009900;\"><strong> <span style=\"font-family:Arial, Verdana, Tahoma, 宋体;line-height:20px;background-color:#FFFFFF;font-size:16px;\">适宜人群：一般人群均可食用。</span><br />\r\n<span style=\"font-family:Arial, Verdana, Tahoma, 宋体;line-height:20px;background-color:#FFFFFF;font-size:16px;\">忌食人群：糖尿病患者，便秘者不宜多吃；脾胃虚寒者不宜多食，多食则令人泄泻。</span><br />\r\n<span style=\"font-family:Arial, Verdana, Tahoma, 宋体;line-height:20px;background-color:#FFFFFF;font-size:16px;\">食物相克：无</span><span style=\"font-size:16px;\"></span><br />\r\n</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;color:#009900;\"><strong><span style=\"font-size:16px;\"><img src=\"file://c:/DOCUME~1/ADMINI~1/APPLIC~1/360se6/USERDA~1/Temp/EAC4B7~1.JPG\" /><img src=\"file://c:/DOCUME~1/ADMINI~1/APPLIC~1/360se6/USERDA~1/Temp/B999A9~1.JPG\" /><img src=\"file://c:/DOCUME~1/ADMINI~1/APPLIC~1/360se6/USERDA~1/Temp/B999A9~1.JPG\" /><img src=\"file://c:/DOCUME~1/ADMINI~1/APPLIC~1/360se6/USERDA~1/Temp/B999A9~1.JPG\" /><img src=\"/include/modules/kindeditor/php/../../../../upfiles/attached/image/20130523/20130523091932_73437.jpg\" width=\"500\" height=\"376\" title=\"www.buyfer.com百惠臻品\" alt=\"www.buyfer.com百惠臻品\" /><br />\r\n</span></strong></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;color:#009900;\"><strong><span style=\"font-size:16px;