INSERT INTO `ot_goods` VALUES ('102', '2013-05-24 23:08:17', '2013-06-19 19:43:54', 'goods', '49', '0', '韭黄', '/500g', '5.98', '1115', '500g', '201305242310105728.jpg', 'a:1:{i:0;s:22:\"201305242310279668.jpg\";}', '', '2403', '<p>\r\n	<span style=\"font-size:18px;color:#E53333;line-height:2;\"><strong>商品详情</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;color:#E53333;\"><strong><img src=\"/upfiles/attached/image/20130524/20130524152642_45016.jpg\" width=\"500\" height=\"500\" title=\"www.buyfer.com 百惠臻品\" align=\"right\" alt=\"www.buyfer.com 百惠臻品\" /> </strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#009900;font-size:14px;line-height:2;\"><strong>品名：</strong></span><span style=\"font-size:14px;color:#009900;line-height:2;\"><strong>韭黄</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#009900;line-height:2;\"><strong>功效：</strong></span><span style=\"font-family:arial, 宋体, sans-serif;font-size:14px;line-height:2;background-color:#FFFFFF;color:#009900;\"><strong>具健胃、提神、止汗固涩、</strong></span><span style=\"color:#009900;line-height:2;\"><strong>补肾</strong></span><span style=\"font-family:arial, 宋体, sans-serif;font-size:14px;line-height:2;background-color:#FFFFFF;color:#009900;\"><strong>助阳、固精</strong></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<h2 class=\"headline-1\" style=\"font-size:18px;font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;\">\r\n	<span class=\"headline-content\" style=\"color:#E53333;line-height:2;\">食疗作用</span> \r\n</h2>\r\n<h3 class=\"headline-2\" style=\"font-size:16px;font-family:Arial;background-color:#FFFFFF;\">\r\n	<span class=\"headline-content\" style=\"color:#009900;font-size:14px;line-height:2;\">1、补肾温阳</span> \r\n</h3>\r\n<h3 class=\"headline-2\" style=\"font-size:16px;font-family:Arial;background-color:#FFFFFF;\">\r\n	<span class=\"headline-content\" style=\"color:#009900;font-size:14px;line-height:2;\">2、益肝健胃</span> \r\n</h3>\r\n<h3 class=\"headline-2\" style=\"font-size:16px;font-family:Arial;background-color:#FFFFFF;\">\r\n	<span class=\"headline-content\" style=\"color:#009900;font-size:14px;line-height:2;\">3、行气理血</span> \r\n</h3>\r\n<h3 class=\"headline-2\" style=\"font-size:16px;font-family:Arial;background-color:#FFFFFF;\">\r\n	<span class=\"headline-content\" style=\"color:#009900;font-size:14px;line-height:2;\">4、</span><span class=\"headline-content\" style=\"color:#009900;font-size:14px;line-height:2;\">润肠通便</span> \r\n</h3>\r\n<p>\r\n	<span class=\"headline-content\" style=\"color:#006600;font-size:14px;line-height:1.5;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#E53333;font-size:18px;line-height:2;\"><strong>适用人群</strong></span> \r\n</p>\r\n<p>\r\n	<span class=\"headline-content\" style=\"color:#E53333;font-family:NSimSun;font-size:14px;line-height:2;\"><strong>【宜】</strong></span> \r\n</p>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;line-height:2;\"><strong>1、一般人群均能食用。</strong></span> \r\n</div>\r\n<p>\r\n	<span style=\"color:#006600;\"><span style=\"line-height:2;color:#009900;\"><strong>2、</strong></span><span style=\"font-size:14px;line-height:2;color:#009900;\"><strong>适宜便秘、产后乳汁不足女性、寒性体质等人群。</strong></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#E53333;font-family:NSimSun;font-size:14px;line-height:2;\"><strong>【忌】</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#009900;font-size:14px;line-height:2;\"><strong>多食会上火且不易消化，因此阴虚火旺、有眼病和胃肠虚弱的人不宜多食。</strong></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;color:#E53333;\"><strong>食谱推荐</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#009900;font-size:14px;background-color:#FFFFFF;font-family:Arial;\"><strong>韭黄炒肉丝</strong></span> \r\n</p>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<strong><span style=\"color:#009900;\">原料：</span></strong><span style=\"color:#009900;\"><strong>韭黄200克、</strong></span><span style=\"color:#009900;\"><strong>猪肉</strong></span><span style=\"color:#009900;\"><strong>100克、蒜、韭黄炒</strong></span><span style=\"color:#009900;\"><strong>肉丝</strong></span><span style=\"color:#009900;\"><strong>头3瓣、青</strong></span><span style=\"color:#009900;\"><strong>辣椒</strong></span><span style=\"color:#009900;\"><strong>1只、盐1/6汤匙、</strong></span><span style=\"color:#009900;\"><strong>鸡蛋清</strong></span><span style=\"color:#009900;\"><strong>1只、</strong></span><span style=\"color:#009900;\"><strong>料酒</strong></span><span style=\"color:#009900;\"><strong>1/2汤匙、油5汤匙、盐1/5汤匙、鸡粉1/2汤匙、料酒1/2汤匙、芝麻油1汤匙。</strong></span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;\"><strong>做法：</strong></span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;\"><strong>1.猪肉洗净切成丝，加1/6汤匙盐、1/2汤匙料酒和1只</strong></span><span style=\"color:#009900;\"><strong>鸡蛋清</strong></span><span style=\"color:#009900;\"><strong>拌匀腌制15分钟。</strong></span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;\"><strong>2.韭黄洗净切段，将韭黄头跟韭黄尾分开；青辣椒切成丝；蒜头拍扁去衣，剁成蓉。</strong></span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;\"><strong>3.锅内烧热2汤匙油，放入腌好的肉丝炒散，洒入1汤匙清水炒至肉色发白，便可捞起待用。</strong></span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;\"><strong>4.添3汤匙油下锅，爆香</strong></span><span style=\"color:#009900;\"><strong>蒜蓉</strong></span><span style=\"color:#009900;\"><strong>和辣椒丝，倒入韭黄头炒30秒，再放入肉丝和韭黄尾，快炒至韭黄变软。</strong></span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;\"><strong>5.加1/5汤匙盐、1/2汤匙鸡粉、1/2汤匙料酒和1汤匙芝麻油翻炒均</strong></span><span style=\"font-family:arial, 宋体, sans-serif;font-size:14px;line-height:21px;background-color:#FFFFFF;color:#009900;\"><strong>匀，即可上桌。</strong></span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<strong><span style=\"color:#009900;\">厨神点拨：</span></strong> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;\"><strong>1.炒韭黄不宜头尾一起放，应先将韭黄头下锅炒一下，再放韭黄尾炒至其发软，这样炒熟的韭黄既不容易夹生，也不会过老。</strong></span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;\"><strong>2.猪肉丝用蛋清腌过，下锅煸炒会嫩滑很多，也可用</strong></span><span style=\"color:#009900;\"><strong>生粉</strong></span><span style=\"color:#009900;\"><strong>替代蛋清来腌制肉丝。</strong></span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;\"><strong>3.此菜下锅清炒的速度要快，尽量减少韭黄尾在锅内逗留的时间，可让成菜鲜嫩多汁。</strong></span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;\"><strong>4.韭黄为</strong></span><span style=\"color:#009900;\"><strong>韭菜</strong></span><span style=\"color:#009900;\"><strong>经软化栽培变黄的产品，它含有蛋白质</strong></span><span style=\"color:#009900;\"><strong>、维生素C等多种营养物质，是主治腹中冷痛、胃中虚热、泄泻、便秘症的良药。</strong></span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;\"><strong>5.宜取新鲜韭黄食用，忌炒熟隔夜食用，此外</strong></span><span style=\"color:#009900;\"><strong>阴虚内热</strong></span><span style=\"color:#009900;\"><strong>、有疮疡者不宜食。</strong></span> \r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '', '64', '0', '1,2,3,4,6', '1', '1', '韭黄|百惠臻品', '韭黄 百惠臻品源自大自然的味道。');
INSERT INTO `ot_goods` VALUES ('93', '2013-05-23 17:05:20', '2013-06-14 08:48:04', 'goods', '54', '0', '铁棍山药', '/500g', '10.98', '1509', '300-500g', '201305231707077191.jpg', 'a:1:{i:0;s:22:\"201305231707198967.jpg\";}', '88,94', '2358', '<p style=\"font-family:Arial, Verdana, Tahoma, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;color:#E53333;font-size:18px;line-height:2;\"><strong>商品详情</strong></span><strong><span style=\"font-family:NSimSun;line-height:2;\">&nbsp; </span><span style=\"line-height:2;\">&nbsp; &nbsp;</span></strong> \r\n</p>\r\n<p style=\"font-family:Arial, Verdana, Tahoma, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#E53333;font-size:18px;\"><strong><img src=\"/upfiles/attached/image/20130523/20130523090856_77329.jpg\" width=\"380\" height=\"380\" title=\"www.buyfer.com  百惠臻品\" align=\"right\" alt=\"www.buyfer.com  百惠臻品\" /></strong></span> \r\n</p>\r\n<p style=\"font-family:Arial, Verdana, Tahoma, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;color:#006600;font-size:14px;\"><strong><span style=\"line-height:2;color:#009900;\">品&nbsp;名：</span></strong><span style=\"line-height:2;color:#009900;\"><strong>铁棍山药&nbsp;</strong></span></span><br />\r\n<span style=\"font-family:NSimSun;color:#006600;font-size:14px;\"><strong><span style=\"line-height:2;color:#009900;\">功&nbsp;效：</span></strong><span style=\"line-height:2;color:#009900;\"><strong>健脾补肺、益胃补肾、固肾益精、聪耳明目、助五脏、强筋骨、长志安神、延年益寿</strong></span></span> \r\n</p>\r\n<p style=\"font-family:Arial, Verdana, Tahoma, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;color:#006600;font-size:14px;\"><span style=\"line-height:2;\"><br />\r\n</span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;color:#E53333;line-height:2;\"><strong>食疗作用</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#009900;font-size:14px;line-height:2;\"><strong>1、</strong></span><span style=\"font-family:arial, 宋体, sans-serif;line-height:2;background-color:#FFFFFF;color:#009900;font-size:14px;\"><strong>补中益气 &nbsp;&nbsp;</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"background-color:#FFFFFF;font-family:arial, 宋体, sans-serif;line-height:2;color:#009900;font-size:14px;\"><strong>2、</strong></span><span style=\"background-color:#FFFFFF;font-family:arial, 宋体, sans-serif;line-height:2;color:#009900;font-size:14px;\"><strong>消渴</strong></span><span style=\"color:#009900;font-size:14px;line-height:2;\"><strong>生津</strong></span><span style=\"background-color:#FFFFFF;font-family:arial, 宋体, sans-serif;line-height:2;color:#009900;font-size:14px;\"><strong>&nbsp; &nbsp;</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"background-color:#FFFFFF;font-family:arial, 宋体, sans-serif;line-height:2;color:#009900;font-size:14px;\"><strong>3、</strong></span><span style=\"background-color:#FFFFFF;font-family:arial, 宋体, sans-serif;line-height:24px;color:#006600;font-size:14px;\"></span><span style=\"background-color:#FFFFFF;font-family:arial, 宋体, sans-serif;line-height:24px;\"><span style=\"color:#009900;font-size:14px;line-height:2;\"><strong>保健主要</strong></span><span style=\"color:#009900;font-size:14px;line-height:2;\"><strong>是抗衰老、增强免疫功能和改善性功能。</strong></span><span style=\"color:#009900;font-size:14px;line-height:2;\"><strong> &nbsp; &nbsp;&nbsp;</strong></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"background-color:#FFFFFF;font-family:arial, 宋体, sans-serif;line-height:2;color:#009900;\"><span style=\"color:#009900;font-size:14px;line-height:2;\"><strong>4、养颜 &nbsp; &nbsp;&nbsp;</strong></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"background-color:#FFFFFF;font-family:arial, 宋体, sans-serif;line-height:2;color:#009900;\"><span style=\"color:#009900;font-size:14px;line-height:2;\"><strong>5、</strong></span></span><span style=\"background-color:#FFFFFF;font-family:arial, 宋体, sans-serif;line-height:24px;color:#006600;font-size:14px;\"></span><span style=\"background-color:#FFFFFF;font-family:arial, 宋体, sans-serif;line-height:2;color:#009900;font-size:14px;\"><strong>特殊药理功能</strong></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:18px;line-height:2;color:#E53333;\"><strong>小贴士</strong></span></span> \r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:14px;line-height:24px;\"> </span></span> \r\n</p>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"line-height:2;color:#009900;\"><strong>铁棍山药辨别方法：</strong></span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<strong><span style=\"line-height:2;color:#009900;\">外观：</span></strong><span style=\"line-height:2;color:#009900;\"><strong>铁棍山药通常直径2—2.5厘米，表皮颜色略深，根茎有铁红色斑痕，若直径太粗</strong></span><span style=\"color:#009900;line-height:2;\"><strong>或表皮过于光滑，说明不正宗。</strong></span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<strong><span style=\"line-height:2;color:#009900;\">重量：</span></strong><span style=\"line-height:2;color:#009900;\"><strong>铁棍山药的单支重量一般不超过250克，也就是五两；太重可能就不是正宗的了。</strong></span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<strong><span style=\"line-height:2;color:#009900;\">肉质：</span></strong><span style=\"line-height:2;color:#009900;\"><strong>将铁棍山药折断，可以感到其肉质较硬，粉性足，其断面细腻，呈白色或略显牙黄色，黏液少；普通山药不仅肉质没有这么坚硬，且粉性也不够足。</strong></span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<strong><span style=\"line-height:2;color:#009900;\">口感：</span></strong><span style=\"line-height:2;color:#009900;\"><strong>由于铁棍山药中水分含量少，山药多糖等含量丰富，因此，其液汁较浓，煮食后口感较干腻、甜香，入口觉得“面而甜”，带皮食用伴随淡淡的麻味。</strong></span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"line-height:2;color:#009900;\"><strong>正宗的“铁棍山药”在山地生长，形状像树根，沙地种植的山药则又长又直；铁棍山药的颜色偏深一些，普通山药的皮则颜色较浅。</strong></span> \r\n</div>\r\n<span style=\"line-height:2;\"></span><br />\r\n<p>\r\n	<br />\r\n</p>', '', '43', '0', '1,2,4,6', '1', '1', '铁棍山药|百惠臻品', '铁棍山药 百惠臻品源自大自然的味道。');
INSERT INTO `ot_goods` VALUES ('94', '2013-05-23 21:21:18', '2013-06-14 09:07:04', 'goods', '54', '0', '芋头', '/500g', '5.98', '1510', '300-700g', '201305232123087301.jpg', 'a:1:{i:0;s:22:\"201305232123322396.jpg\";}', '', '2363', '<p style=\"font-family:Arial, Verdana, Tahoma, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#E53333;font-size:18px;line-height:2;\"><strong>商品详情</strong></span> \r\n</p>\r\n<p style=\"font-family:Arial, Verdana, Tahoma, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#E53333;font-size:18px;\"><img src=\"http://www.justeasy.com.cn/img/upload/20120518/20081222155187.jpg\" width=\"450\" height=\"326\" title=\"www.buyfer.com 百惠臻品\" align=\"right\" alt=\"www.buyfer.com 百惠臻品\" /></span> \r\n</p>\r\n<p style=\"font-family:Arial, Verdana, Tahoma, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#006600;\"><span style=\"font-family:NSimSun;font-size:14px;line-height:2;color:#009900;\"><strong>品&nbsp;&nbsp;</strong></span><span style=\"font-family:NSimSun;font-size:14px;\"><strong><span style=\"line-height:2;color:#009900;\">名：</span></strong><span style=\"line-height:2;color:#009900;\"><strong>芋头</strong></span></span></span> \r\n</p>\r\n<p style=\"font-family:Arial, Verdana, Tahoma, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;color:#009900;font-size:14px;line-height:2;\"><span style=\"font-family:NSimSun;font-size:14px;line-height:2;color:#009900;\"><strong>功&nbsp; 效：</strong></span></span><span style=\"font-family:NSimSun;font-size:14px;line-height:2;color:#009900;\"><strong>&nbsp;</strong></span><span style=\"color:#009900;font-family:NSimSun;font-size:14px;line-height:2;\"><strong>芋头有开胃生津、消炎镇痛、补气益肾等功效，可治胃痛、痢疾、慢性肾炎等。</strong></span> \r\n</p>\r\n<p style=\"font-family:Arial, Verdana, Tahoma, 宋体;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"font-family:Arial, Verdana, Tahoma, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:\'Microsoft YaHei\';color:#E53333;font-size:18px;line-height:2;\"><strong>适宜人群</strong></span> \r\n</p>\r\n<p style=\"font-family:Arial, Verdana, Tahoma, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"line-height:2;font-family:NSimSun;font-size:14px;color:#E53333;\"><strong>【宜】</strong></span> \r\n</p>\r\n<p style=\"font-family:Arial, Verdana, Tahoma, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:NSimSun;color:#009900;font-size:14px;line-height:2;\"><span style=\"color:#009900;font-size:14px;font-family:NSimSun;line-height:2;\"><strong>一般人群均可食用，</strong></span></span> \r\n</p>\r\n<p style=\"font-family:Arial, Verdana, Tahoma, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;font-size:14px;font-family:NSimSun;line-height:2;\"><strong>特别适合身体虚弱者食用；</strong></span> \r\n</p>\r\n<p style=\"font-family:Arial, Verdana, Tahoma, 宋体;background-color:#FFFFFF;\">\r\n	<strong><span style=\"color:#E53333;font-size:14px;line-height:2;\">【忌】</span></strong> \r\n</p>\r\n<p style=\"font-family:Arial, Verdana, Tahoma, 宋体;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;font-size:14px;line-height:2;\"><span style=\"color:#009900;font-family:NSimSun;font-size:14px;line-height:28px;background-color:#FFFFFF;\"><strong>过敏性体质者、小儿食滞、胃纳欠佳、糖尿病患者</strong></span></span> \r\n</p>\r\n<p style=\"font-family:Arial, Verdana, Tahoma, 宋体;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<span style=\"line-height:20px;background-color:#FFFFFF;font-family:\'Microsoft YaHei\';color:#006600;font-size:16px;\"><span> \r\n<p>\r\n	<span style=\"font-size:18px;line-height:2;\"><strong><span style=\"color:#E53333;line-height:2;\">商品特色</span></strong></span> \r\n</p>\r\n<p>\r\n	<span><span><span style=\"font-family:NSimSun;font-size:14px;line-height:2;color:#009900;\"><strong>1.芋头中富含蛋白质、钙、磷、铁、钾、镁、钠、胡萝卜素、烟酸、维生素C、B族维生素、皂角甙等多种成分，所含的矿物质中，氟的含量较高，具有洁齿防龋、保护牙齿的作用。&nbsp;　　</strong></span><br />\r\n<span><span style=\"font-fam