INSERT INTO `ot_goods` VALUES ('179', '2013-05-30 22:05:37', '2013-06-16 09:46:27', 'goods', '58', '0', '芭乐', '', '0.00', '2417', '500g', '201305302209054061.jpg', 'a:2:{i:0;s:22:\"201305302208416922.jpg\";i:1;s:22:\"201305302208557537.jpg\";}', '10,22', '2906', '<p class=\"MsoNormal\">\r\n	<br />\r\n<img src=\"/include/modules/kindeditor/php/../../../../upfiles/attached/image/20130530/20130530143413_97610.jpg\" width=\"602\" height=\"602\" title=\"www.buyfer.com百惠臻品\" align=\"right\" alt=\"www.buyfer.com百惠臻品\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;color:#E53333;line-height:2.5;\"><strong>商品介绍：</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;color:#009900;line-height:2.5;\"><strong>芭乐又名番石榴，为热带</strong></span><span style=\"font-size:16px;color:#009900;line-height:2.5;\"><strong>果树，原产热带美洲，今在元江</strong></span><span style=\"font-size:16px;color:#009900;line-height:2.5;\"><strong>有栽培。番石榴属桃金娘科番石榴，是常绿小乔木</strong></span><span style=\"font-size:16px;color:#009900;line-height:2.5;\"><strong>或灌木</strong></span><span style=\"font-size:16px;color:#009900;line-height:2.5;\"><strong>，俗称拔番石榴子</strong></span><span style=\"font-size:16px;color:#009900;line-height:2.5;\"><strong>、那拔。因其来自国外，故名为番石榴，是亚热带</strong></span><span style=\"font-size:16px;color:#009900;line-height:2.5;\"><strong>名优水果品种之一。</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:18px;color:#009900;line-height:2.5;\"><strong><br />\r\n</strong></span> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<strong><br />\r\n</strong> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<strong><br />\r\n</strong> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<strong><span style=\"font-size:18px;color:#E53333;\">营养价值：</span></strong> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>芭乐营养丰富，可增加食欲，促进儿童生长发育，含有蛋白质、脂肪、糖类、维生素A、B族维生素、维C，钙、磷、铁。以维生素c</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>而言，比柑桔、香蕉、木瓜、番茄、西瓜、凤梨都高，种子中铁的含量更胜于其它水果，所以最好能一起食下去。番石榴既可鲜食，也可榨汁饮用。多吃可以预防老化，排除体内毒素的功效。另外还可防治高血压、糖尿病，对于肥胖症及肠胃不佳之患者，最为理想之食用水果。此外番石榴的叶片和幼果切片晒干泡茶喝，可辅助治疗糖尿病</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>。</strong></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"background-color:white;\">\r\n	<b><span style=\"font-size:18px;line-height:1.5;color:#E53333;\">适用人群：</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"background-color:white;\">\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>一般人群均可食用，特别适合生长发育期的儿童、对于高血压、糖尿病、肥胖症及肠胃不佳者，是为理想之食用水果。儿童及有便秘习惯或有内热的人不宜多吃。肝热的人应慎防便秘，因为番石榴</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>具有收敛止泻作用。</strong></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"/include/modules/kindeditor/php/../../../../upfiles/attached/image/20130530/20130530143413_27726.jpg\" width=\"969\" height=\"1001\" title=\"www.buyfer.com百惠臻品\" alt=\"www.buyfer.com百惠臻品\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<img src=\"/include/modules/kindeditor/php/../../../../upfiles/attached/image/20130530/20130530143413_39385.jpg\" width=\"1023\" height=\"727\" title=\"www.buyfer.com百惠臻品\" alt=\"www.buyfer.com百惠臻品\" /> \r\n</p>\r\n<p class=\"MsoNormal\">\r\n	<br />\r\n</p>', '<p>\r\n	品名：芭乐，番石榴。\r\n</p>\r\n<p>\r\n	规格：500g\r\n</p>\r\n<p>\r\n	储存方式：常温\r\n</p>', '19', '0', '', '1', '0', '', '');
INSERT INTO `ot_goods` VALUES ('216', '2013-06-14 22:24:24', '2013-06-14 22:36:34', 'goods', '53', '0', '茶树菇', '/500g', '6.98', '1402', '500g', '201306142226296058.jpg', 'a:2:{i:0;s:22:\"201306142228221494.jpg\";i:1;s:22:\"201306142228342118.jpg\";}', '', '3076', '<p>\r\n	<span style=\"font-family:NSimSun;font-size:18px;color:#E53333;line-height:2;\"><strong>商品详情</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;color:#009900;\"><strong><span style=\"line-height:2;\">品名：茶树菇</span><img src=\"http://www.buyfer.com/upfiles/attached/image/20130614/20130614142149_22499.jpg\" width=\"400\" height=\"300\" title=\"www.buyfer.com 百惠臻品\" align=\"right\" alt=\"www.buyfer.com 百惠臻品\" /></strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>属性：</strong></span><span style=\"font-family:arial, 宋体, sans-serif;background-color:#FFFFFF;\"><span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>茶树菇是</strong></span><span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>担子菌亚门</strong></span><span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>，担子纲、蘑菇菌目、粪伞科、田头菇属，又名</strong></span><span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>柱状田头菇</strong></span><span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>、</strong></span><span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>杨树菇</strong></span><span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>、茶薪菇、柱状环锈伞、</strong></span><span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>柳</strong></span><span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>松茸</strong></span><span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>等，原为</strong></span><span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>江西</strong></span><span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>广昌境内高山密林地区茶树蔸部生长的一种野生</strong></span><span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>蕈菌</strong></span><span><span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>。</strong></span><br />\r\n</span><span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>经过优化改良的茶树菇，盖嫩柄脆，味纯清香，口感极佳，可烹制成各种美味佳肴，其营养价值超过</strong></span><span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>香菇</strong></span><span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>等其他食用菌，属高档</strong></span><span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>食用菌</strong></span><span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>类。</strong></span></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:18px;line-height:2;color:#E53333;\"><strong>营养价值</strong></span></span> \r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:14px;line-height:25px;\"><span style=\"line-height:24px;background-color:#FFFFFF;\"><span style=\"color:#009900;line-height:2;\"><strong>茶树菇营养丰富，所含蛋白质中有18种氨基酸，其中含量最高的是蛋氨酸占</strong></span><br />\r\n</span><span style=\"line-height:2;background-color:#FFFFFF;color:#009900;\"><strong>2.49%，其次为谷氨酸、天门冬氨酸、异亮氨酸、甘氨酸和丙氨酸。总氨基酸含量为16.86%。人体必需的8种氨基酸含量齐全，并且有丰富的</strong></span><span style=\"color:#009900;line-height:2;\"><strong>B族维生素</strong></span><span style=\"line-height:2;background-color:#FFFFFF;color:#009900;\"><strong>和钾、钠、钙、镁、铁、锌等矿质元素。</strong></span><span style=\"line-height:2;\"></span><br />\r\n</span></span> \r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:14px;line-height:25px;\"><span style=\"line-height:2;\"><br />\r\n</span></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:2;\"><span style=\"font-size:18px;line-height:2;color:#E53333;\"><strong>食疗作用</strong></span></span> \r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:14px;line-height:25px;\"></span></span> \r\n</p>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;line-height:2;\"><strong>1.防癌抗癌</strong></span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;line-height:2;\"><strong>茶树菇含大量抗癌多糖，有很好的抗癌作用，人们把它称做“中华神菇”、“保健食品”、“抗癌尖兵”。</strong></span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;line-height:2;\"><strong>2.抗衰老</strong></span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;line-height:2;\"><strong>具有补肾滋阴、健脾胃、提高人体免疫力、增强人体防病能力的功效。常食可起到抗衰老、美容等作用。</strong></span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;line-height:2;\"><strong>3.缓解小儿尿床</strong></span> \r\n</div>\r\n<p>\r\n	<span><strong><span style=\"color:#009900;font-size:14px;\">茶树菇对肾虚尿频、水肿、气喘，尤其小儿</span></strong><strong><span style=\"color:#009900;font-size:14px;\">低热</span></strong><strong><span style=\"color:#009900;font-size:14px;\">尿床，有独特疗效。</span></strong></span> \r\n</p>\r\n<p>\r\n	<span><strong><br />\r\n</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#E53333;line-height:2.5;font-size:18px;\"><strong>食谱推荐</strong></span>\r\n</p>\r\n<p>\r\n	<span style=\"color:#E53333;font-size:14px;line-height:2.5;\"><strong>茶树菇炖鸡肉</strong></span> \r\n</p>\r\n<h4 style=\"color:#FF3300;\">\r\n	<p>\r\n		<span><img src=\"http://www.buyfer.com/upfiles/attached/image/20130614/20130614141236_50896.jpg\" alt=\"\" /><br />\r\n</span> \r\n	</p>\r\n</h4>\r\n<h4 style=\"color:#FF3300;font-size:14px;background-color:#F3F3F3;\">\r\n	<span></span> \r\n</h4>\r\n<h4 style=\"color:#FF3300;\">\r\n	<span>制作步骤</span> \r\n</h4>\r\n<ul>\r\n	<li class=\"recipe3r\">\r\n		<span><img src=\"http://i3.meishichina.com/attachment/recipe/200912/m_200912282126559.jpg\" width=\"180\" border=\"0\" alt=\"茶树菇炖鸡肉的做法第1步\" /> \r\n		<div class=\"lrlstepcon\" style=\"margin:5px 0px 0px 10px;padding:0px;\">\r\n			1.&nbsp;小鸡半只（因为我买的是冷冻的，所以泡姜水里半小时）。\r\n		</div>\r\n</span> \r\n	</li>\r\n</ul>\r\n<ul>\r\n	<li class=\"recipe3r\">\r\n		<span><img src=\"http://i3.meishichina.com/attachment/recipe/200912/m_200912282127442.jpg\" width=\"180\" border=\"0\" alt=\"茶树菇炖鸡肉的做法第2步\" /> \r\n		<div class=\"lrlstepcon\" style=\"margin:5px 0px 0px 10px;padding:0px;\">\r\n			2.&nbsp;捞出斩块，烧花椒水，水开放鸡肉，料酒一汤匙，不要盖锅盖，煮三分钟，捞出过凉水洗干净。\r\n		</div>\r\n</span> \r\n	</li>\r\n</ul>\r\n<ul>\r\n	<li class=\"recipe3r\">\r\n		<span><img src=\"http://i3.meishichina.com/attachment/recipe/200912/m_200912282131398.jpg\" width=\"180\" border=\"0\" alt=\"茶树菇炖鸡肉的做法第3步\" /> \r\n		<div class=\"lrlstepcon\" style=\"margin:5px 0px 0px 10px;padding:0px;\">\r\n			3.&nbsp;茶树菇泡软（泡的方法可以先洗干净，放温水里，小火煮开，然后泡到半小时，因为茶树菇有点硬，不泡好口感要逊色）。\r\n		</div>\r\n</span> \r\n	</li>\r\n</ul>\r\n<ul>\r\n	<li class=\"recipe3r\">\r\n		<span><img src=\"http://i3.meishichina.com/attachment/recipe/200912/m_200912282131053.jpg\" width=\"180\" border=\"0\" alt=\"茶树菇炖鸡肉的做法第4步\" /> \r\n		<div class=\"lrlstepcon\" style=\"margin:5px 0px 0px 10px;padding:0px;\">\r\n			4.&nbsp;锅里烧三汤匙油，大火煸炒鸡肉，鸡肉皮发黄，肉稍干。\r\n		</div>\r\n</span> \r\n	</li>\r\n</ul>\r\n<ul>\r\n	<li class=\"recipe3r\">\r\n		<span><img src=\"http://i3.meishichina.com/attachment/recipe/200912/m_200912282132094.jpg\" width=\"180\" border=\"0\" alt=\"茶树菇炖鸡肉的做法第5步\" /> \r\n		<div class=\"lrlstepcon\" style=\"margin:5px 0px 0px 10px;padding:0px;\">\r\n			5.&nbsp;下葱姜蒜香叶等煸炒出香味，放一汤匙酱油，半汤匙糖，半汤匙白酒（可以用别的酒代替，小时候农村是没有料酒用的）。\r\n		</div>\r\n</span> \r\n	</li>\r\n</ul>\r\n<ul>\r\n	<li class=\"recipe3r\">\r\n		<span><img src=\"http://i3.meishichina.com/attachment/recipe/200912/m_200912282132420.jpg\" width=\"180\" border=\"0\" alt=\"茶树菇炖鸡肉的做法第6步\" /> \r\n		<div class=\"lrlstepcon\" style=\"margin:5px 0px 0px 10px;padding:0px;\">\r\n			6.&nbsp;放入茶树菇，一起炒一会，兑温水，大火烧开，小火炖四十分钟到一个半小时。放盐，五香粉，味精，再中火煮一会，出锅撒香菜香葱末。\r\n		</div>\r\n</span> \r\n	</li>\r\n</ul>\r\n<ul>\r\n</ul>\r\n<div style=\"margin:25px 0px 0px;padding:0px;font-family:Arial, Helvetica, sans-serif;background-color:#FFFFFF;\">\r\n	<span><br class=\"Apple-interchange-newline\" />\r\n</span> \r\n</div>\r\n<span><br />\r\n<br />\r\n</span> \r\n<p>\r\n	<span><span style=\"font-size:14px;line-height:25px;\"></span></span> \r\n</p>', '', '27', '0', '', '1', '1', '茶树菇', '茶树菇');
INSERT INTO `ot_goods` VALUES ('217', '2013-06-14 22:54:05', '2013-06-17 16:43:14', 'goods', '55', '0', '长香玉甜瓜', '/500g', '4.60', '2205', '2000g-3000g', '201306142257107776.jpg', 'a:3:{i:0;s:22:\"201306142258224679.jpg\";i:1;s:22:\"201306142258552244.jpg\";i:2;s:22:\"201306142301334497.jpg\";}', '', '3081', '<p>\r\n	<img src=\"/include/modules/kindeditor/php/../../../../upfiles/attached/image/20130614/20130614150249_85874.jpg\" width=\"350\" height=\"350\" title=\"www.buyfer.com百惠臻品\" alt=\"www.buyfer.com百惠臻品\" /> \r\n</p>\r\n<p>\r\n	<span style=\"color:#009900;font-size:16px;\"><strong>长香玉瓜皮灰绿色，有白细网纹。肉橙红色，肉质脆甜有香味，含糖量为16%～18%。单瓜重2～3千克。长香玉甜瓜相对于黄皮哈密瓜，口感更脆。</strong></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"http://www.buyfer.com/upfiles/attached/image/20130614/20130614150251_96789.jpg\" width=\"349\" height=\"350\" title=\"www.buyfer.com百惠臻品\" alt=\"www.buyfer.com百惠臻品\" /> \r\n</p>\r\n<p style=\"color:#4F4F4F;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"line-height:24px;font-size:18px;color:#E53333;\"><b>挑选窍门：</b></span> \r\n</p>\r\n<p style=\"color:#4F4F4F;font-family:宋体;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"font-size:16px;color:#009900;\"><strong>长香玉多为椭圆形或橄榄形。它的颜色为果绿色带网纹的。用鼻子</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>去闻瓜，一般有香味</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>的，成熟度适中。没有香味</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>或香味淡薄的，是成熟度较差的，可以放些时候再吃。挑瓜时可以用手摸一摸，如果瓜身坚实微软，成熟度就比较适中。如果太硬则不太熟，太软就是成熟过度。</strong></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"/include/modules/kindeditor/php/../../../../upfiles/attached/image/20130614/20130614150304_34796.jpg\" width=\"306\" height=\"305\" title=\"www.buyfer.com百惠臻品\" alt=\"www.buyfer.com百惠臻品\" /> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"color:#4F4F4F;font-family:宋体;font-size:14px;background-color:white;\">\r\n	<b><span style=\"font-size:18px;color:#E53333;\">食疗作用：</span></b> \r\n</p>\r\n<p class=\"MsoNormal\" align=\"left\" style=\"color:#4F4F4F;font-family:宋体;font-size:14px;background-color:white;\">\r\n	<span style=\"font-size:16px;color:#009900;\"><strong>哈密瓜，性寒味甘，含蛋白质、膳食纤维、胡萝卜素</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>、果胶</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>、糖类</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>、维生素A、维生素B、维生素C、磷、钠、钾等。哈密瓜果肉</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>有利小便、止咳</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>、除烦热、防暑气等作用，可治发烧</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>、中暑、口渴</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>、尿路感染</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>、口鼻生疮等症状并且有清凉消暑</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>，除烦热，生津止渴的作用，是夏季解暑的佳品。</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>食用哈密瓜对人体造血机能有显著的促进作用，可以用来作为贫血的食疗之品。如果常感到身 心疲倦、心神焦躁不安</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>或是口臭</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>，食用哈密瓜都能有所改善。</strong></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '', '22', '0', '', '1', '1', '长香玉甜瓜|长香玉|哈密瓜', '长香玉甜瓜|长香玉|哈密瓜');
INSERT INTO `ot_goods` VALUES ('218', '2013-06-14 23:36:30', '2013-06-17 16:42:37', 'goods', '55', '0', '黑美人西瓜', '/500g', '1.90', '2206', '2500g-3500g', '201306142338257409.jpg', 'a:3:{i:0;s:22:\"201306142338392381.jpg\";i:1;s:22:\"201306142339064204.jpg\";i:2;s:22:\"201306142339264348.jpg\";}', '', '3086', '<p>\r\n	<img src=\"/include/modules/kindeditor/php/../../../../upfiles/attached/image/20130614/20130614154015_80639.jpg\" width=\"341\" height=\"350\" title=\"www.buyfer.com百惠臻品\" alt=\"www.buyfer.com百惠臻品\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;color:#009900;\"><strong>黑美人</strong></span><span style=\"font-size:16px;color:#009900;\"><strong>果皮深黑绿色，故名黑美人。有不明显色条纹。夏季收获皮色较浅，外观十分优美，果重2.5公斤左右。果肉红色肉质鲜嫩多汁，糖度高达14-15ºC左右。果实至瓜尾品质依然甜美。</strong></span> \r\n</p>\r\n<p>\r\n	<img src=\"http://www.buyfer.com/upfiles/attached/image/20130614/20130614153955_83429.jpg\" width=\"350\" height=\"262\" title=\"www.buyfer.com百惠臻品\" alt=\"www.buyfer.com百惠臻品\" /> \r\n</p>', '', '31', '0', '1', '1', '1', '黑美人西瓜|西瓜', '黑美人西瓜|西瓜');
