INSERT INTO `ot_goods` VALUES ('220', '2013-06-15 15:32:19', '2013-06-15 15:56:29', 'goods', '53', '0', '香菇', '/500g', '9.98', '1404', '450-500g', '201306151537468750.jpg', 'a:1:{i:0;s:22:\"201306151534369476.jpg\";}', '', '3096', '<p>\r\n	<span style=\"font-family:NSimSun;font-size:18px;color:#E53333;line-height:2;\"><strong>商品详情</strong></span><span style=\"font-size:18px;font-family:NSimSun;color:#E53333;line-height:2;\"><strong><img src=\"/upfiles/attached/image/20130615/20130615075335_26105.jpg\" width=\"448\" height=\"448\" title=\"www.buyfer.com 百惠臻品\" align=\"right\" alt=\"www.buyfer.com 百惠臻品\" /></strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:NSimSun;color:#009900;line-height:2;\"><strong>品名：</strong></span><span style=\"font-size:14px;font-family:NSimSun;color:#009900;line-height:2;\"><strong>香菇</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:18px;color:#E53333;\"><strong><br />\r\n</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:18px;color:#E53333;line-height:2;\"><strong>商品特色</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>香菇</strong></span><span style=\"font-family:NSimSun;line-height:2;background-color:#FFFFFF;font-size:14px;color:#009900;\"><strong>，又称</strong></span><span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>香蕈</strong></span><span style=\"font-family:NSimSun;line-height:2;background-color:#FFFFFF;font-size:14px;color:#009900;\"><strong>、</strong></span><span style=\"font-family:NSimSun;font-size:14px;color:#009900;line-height:2;\"><strong>冬菇</strong></span><span style=\"font-family:NSimSun;line-height:2;background-color:#FFFFFF;font-size:14px;color:#009900;\"><strong>，是一种生长在木材上的真菌类。由于它味道鲜美，香气沁人，营养丰富，不但位列草菇、平菇、白蘑菇之上，而且素有“真菌皇后”之誉。</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:18px;line-height:24px;background-color:#FFFFFF;color:#E53333;\"><strong><br />\r\n</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:18px;line-height:2;background-color:#FFFFFF;color:#E53333;\"><strong>食疗作用</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:24px;background-color:#FFFFFF;\"><span style=\"font-size:14px;color:#009900;line-height:2;\"><strong>1、</strong></span><span style=\"line-height:2;background-color:#FFFFFF;font-size:14px;color:#009900;\"><strong>降低血脂</strong></span><span style=\"font-size:14px;color:#009900;line-height:2;\"><strong></strong></span></span> \r\n</p>\r\n<p>\r\n	<span><span style=\"line-height:24px;\"><span style=\"font-size:14px;color:#009900;line-height:2;\"><strong>2、</strong></span><span style=\"line-height:2;background-color:#FFFFFF;font-size:14px;color:#009900;\"><strong>预防人体各种粘膜及皮肤炎病</strong></span></span></span> \r\n</p>\r\n<p>\r\n	<span><span style=\"line-height:24px;\"><span style=\"font-size:14px;color:#009900;line-height:2;\"><strong>3、</strong></span><span style=\"line-height:2;background-color:#FFFFFF;font-size:14px;color:#009900;\"><strong>预防血管硬化，可降低人的</strong></span><span style=\"font-size:14px;color:#009900;line-height:2;\"><strong>血压</strong></span><span style=\"font-size:14px;color:#009900;line-height:2;\"><strong></strong></span></span></span> \r\n</p>\r\n<p>\r\n	<span><span style=\"line-height:24px;\"><span style=\"font-size:14px;color:#009900;line-height:2;\"><strong>4、</strong></span><span style=\"line-height:2;background-color:#FFFFFF;font-size:14px;color:#009900;\"><strong>防止酸性</strong></span><span style=\"font-size:14px;color:#009900;line-height:2;\"><strong>食物中毒</strong></span><span style=\"font-size:14px;color:#009900;line-height:2;\"><strong></strong></span></span></span> \r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:18px;line-height:24px;color:#E53333;\"><strong><br />\r\n</strong></span></span> \r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:18px;line-height:2;color:#E53333;\"><strong>适宜人群</strong></span></span> \r\n</p>\r\n<p>\r\n	<span><span style=\"font-size:14px;line-height:24px;\"> </span></span> \r\n</p>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<strong><span style=\"color:#009900;line-height:2;\">一般人群均可食用。</span></strong> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#E53333;line-height:2;\"><strong>【宜】</strong></span><strong><span style=\"color:#009900;line-height:2;\">气虚</span></strong><strong><span style=\"color:#009900;line-height:2;\">头晕、</span></strong><strong><span style=\"color:#009900;line-height:2;\">贫血</span></strong><strong><span style=\"color:#009900;line-height:2;\">、白血球减少，自身</span></strong><strong><span style=\"color:#009900;line-height:2;\">抵抗力</span></strong><strong><span style=\"color:#009900;line-height:2;\">下降以及年老体弱者宜食；</span></strong><strong><span style=\"color:#009900;line-height:2;\">高脂血症</span></strong><strong><span style=\"color:#009900;line-height:2;\">，高血压病，动脉硬化症 ，糖尿病，肥胖者宜食；癌症病人及癌症患者放疗、化疗后宜食；急慢性肝炎，</span></strong><strong><span style=\"color:#009900;line-height:2;\">脂肪肝</span></strong><strong><span style=\"color:#009900;line-height:2;\">，胆结石，便秘者 宜食；</span></strong><strong><span style=\"color:#009900;line-height:2;\">小儿麻疹</span></strong><strong><span style=\"color:#009900;line-height:2;\">透发不快及</span></strong><strong><span style=\"color:#009900;line-height:2;\">小儿佝偻病</span></strong><strong><span style=\"color:#009900;line-height:2;\">者宜食；肾炎病人宜食。</span></strong> \r\n</div>\r\n<p>\r\n	<strong><span style=\"color:#009900;\"><span style=\"color:#E53333;line-height:2;\">【忌】</span><span style=\"line-height:2;\">香菇为动风食物，顽固性皮肤瘙痒症患者</span></span></strong><strong><span style=\"color:#009900;line-height:2;\">忌食</span></strong><strong><span style=\"color:#009900;line-height:2;\">。脾胃寒湿气滞或皮肤骚痒病患者忌食。</span></strong> \r\n</p>\r\n<p>\r\n	<strong><br />\r\n</strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"line-height:2;font-size:18px;color:#E53333;\">食谱推荐</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"color:#009900;line-height:2;background-color:#FFFFFF;font-family:Arial;font-size:16px;\"><strong>香菇鸡汤</strong></span>\r\n</p>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<b><span style=\"color:#009900;line-height:2;\">原料：</span></b><span style=\"color:#009900;line-height:2;\">鸡大腿、香菇、笋子。</span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<b><span style=\"color:#009900;line-height:2;\">做法：</span></b> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;line-height:2;\">1、香菇用水泡个20分钟，然后去蒂，对半切。</span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;line-height:2;\">2、这个笋子是老板推荐给我的，叫毛尖笋吧，也可以用其它笋子代替的。不必太拘泥，今天主角是香菇和鸡肉。笋子泡一下，然后切段。</span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;line-height:2;\">3、鸡大腿肉，洗干净，切块，稍微大点块无所谓。</span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;line-height:2;\">4、锅中水烧开，川烫3到4分钟去血水，然后捞出。</span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;line-height:2;\">5、锅中加冷说，到入去血水的鸡块，放点姜片，加点白酒。</span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;line-height:2;\">6、等锅中水开时倒入切好的的香菇，放些盐。</span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;line-height:2;\">7、锅中水再次沸腾的时候加入切段好的笋子。</span> \r\n</div>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<span style=\"color:#009900;line-height:2;\">8、大火烧开，小火慢慢煨个30到40分钟，鸡肉烂的时候就可以出锅了，如果不咸的话自己再加点盐，这个自己用勺子尝尝烫就行了。加点葱末就可以出锅了。</span> \r\n</div>\r\n<span style=\"color:#009900;line-height:2;\"></span> \r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\"><br />\r\n</span> \r\n</p>', '', '44', '0', '1,2', '1', '1', '香菇', '香菇，又称香蕈、冬菇，是一种生长在木材上的真菌类。由于它味道鲜美，香气沁人，营养丰富，不但位列草菇、平菇、白蘑菇之上，而且素有“真菌皇后”之誉。');
INSERT INTO `ot_goods` VALUES ('221', '2013-06-15 15:56:29', '2013-06-16 20:12:45', 'goods', '58', '0', '榴莲', '/500g', '12.90', '2416', '2000g-3000g', '201306151631213950.jpg', 'a:3:{i:0;s:22:\"201306151658285768.jpg\";i:1;s:22:\"201306151635258923.jpg\";i:2;s:22:\"201306151635455984.jpg\";}', '53,19', '3101', '<p>\r\n	<img src=\"/include/modules/kindeditor/php/../../../../upfiles/attached/image/20130615/20130615083618_50698.jpg\" width=\"350\" height=\"296\" title=\"www.buyfer.com百惠臻品\" align=\"left\" alt=\"www.buyfer.com百惠臻品\" /> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;color:#E53333;line-height:1.5;\"><strong>商品介绍：</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>榴莲果实有足球</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>大小，果皮坚实，密生三角形</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>刺，果肉</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>是由假种皮</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>的肉包组成，肉色淡黄，粘性多汁，头一次食用榴莲时，那种异常的气味可使许多人“望而却步</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>”，但是，也有许多人自从吃了第一口以后，感到酥软味甜，吃起来有冰激凌</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>的口感，就会被榴莲那种特殊的回味和质感</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>所吸引。榴莲果肉含有多种维生素</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>，营养丰富，香味独特，具有“水果之王</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>”的美称。</strong></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>中文名“榴莲”并非源于“流连忘返”，“榴莲”是外来词汇，同“咖啡</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>”一样是音译词。让你吃完以后‘流连忘返’。</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>榴莲是驰名的优质佳果。成熟果肉浅黄，黏性多汁，酥软味甜，吃起来具有陈乳酪和洋葱味，初尝似有异味，续食清凉甜蜜，回味甚佳，故有“流连（榴莲）忘返”的美誉。别名又叫韶子。它原产于东南亚，被称为“万果之王”。榴莲成熟后自己落下，通常都是在深夜或清晨掉落。</strong></span> \r\n</p>\r\n<p>\r\n	<strong><br />\r\n</strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;color:#E53333;\">食用：</span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>一个榴莲是否可口，除关系到品种</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>、产地</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>、气候</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>、施肥及摘采时间外，适时剖开取食也极为重要。尚未成熟即剖食，榴莲肉硬而淡，味同嚼蜡，便不会好吃。用小竹条轻敲榴莲，听其声音，以辨别成熟程度，同时以嗅闻的方法决定是否可以剖开。</strong></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<div class=\"para\">\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>买回家的榴莲应用报纸严密包裹起来，免得它的刺扎伤小孩。榴莲具有后熟作用，应将其放在阴凉处保存，成熟后的果实会裂开，这时可将果肉取出放入保鲜袋后于冰箱里保存。</strong></span> \r\n	<div class=\"text_pic layoutright\">\r\n		<p class=\"pic-info\">\r\n			<br />\r\n		</p>\r\n	</div>\r\n</div>\r\n<p>\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>榴莲虽然好吃，但不可一次吃得太多，不然容易导致身体燥热，其丰富的营养</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>还会因肠胃</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>无法完全吸收而引起上火。消除燥热的方法是在吃榴莲的同时喝些淡盐水或吃些水分比较多的水果</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>来平衡，梨</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>、西瓜</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>都是很好的选择。不过，榴莲的最好搭档是被称为水果皇后的山竹</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>，只有它才能轻易降伏水果之王的火气，保护身体不受损害。</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong><br />\r\n</strong></span> \r\n</p>\r\n<span style=\"font-size:18px;color:#E53333;line-height:1.5;\"><strong></strong></span><strong> \r\n<h3 class=\"headline-2\">\r\n	<span class=\"headline-content\" style=\"font-size:18px;line-height:1.5;color:#E53333;\">注意事项:</span> \r\n</h3>\r\n</strong><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong> \r\n<div class=\"para\">\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>1、病后及妇女产后可用之来补养身体；</strong></span> \r\n</div>\r\n<div class=\"para\">\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>2、糖尿病</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>患者忌食，肾病</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>和心脏病</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>人慎食。榴莲含糖和胆固醇</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>较多，因而糖尿病人、高胆固醇血症患者应忌食或少食榴莲；榴莲含钾量较高，因而肾病及心脏病人应少吃；</strong></span> \r\n</div>\r\n<div class=\"para\">\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>3、从中医角度来看，榴莲性热而滞，喉痛、咳嗽</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>、感冒</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>、阴虚体质、气管敏感者均不宜多食榴莲；</strong></span> \r\n</div>\r\n<div class=\"para\">\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>4、榴莲果汁</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>黏稠，易阻塞咽喉、气管而引起窒息，故老人须少吃、慢吃；</strong></span> \r\n</div>\r\n<div class=\"para\">\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>5、榴莲富含纤维素，在肠胃中会吸水膨胀，过多食用会阻塞肠道，引起便秘。</strong></span> \r\n</div>\r\n</strong></span> \r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;color:#E53333;\">存放:</span></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#009900;font-size:16px;\">一、可以将果肉取出，放入保鲜袋后于冰箱里保存，冰镇后会具有雪糕的口感。如果果肉变馊则说明榴莲已变质，不宜继续食用；</span></strong> \r\n</p>\r\n<div class=\"para\">\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>二、最好是放在保鲜盒里后放到冰箱的保鲜层，不过就算是新鲜的榴莲也最多放5天左右吧，存放时间长了就会影响新鲜感，还是少买快吃吧。</strong></span> \r\n</div>\r\n<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong></strong></span> \r\n<h2 class=\"headline-1\">\r\n	<strong><span style=\"font-size:18px;color:#E53333;\"><br />\r\n</span></strong> \r\n</h2>\r\n<h2 class=\"headline-1\">\r\n	<strong><span style=\"font-size:18px;color:#E53333;\">选择榴莲:</span></strong> \r\n</h2>\r\n<h2 class=\"headline-1\">\r\n	<strong><span style=\"color:#009900;\">一、选榴莲先看它的外观，黄色就是熟的表现，青的还是生的。</span></strong> \r\n</h2>\r\n<div class=\"para\">\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>二、用两手指在榴莲的刺往内挤一下，如果手感有弹性，两棵刺能往里靠的话，那榴莲也就差不多是熟的了。</strong></span> \r\n</div>\r\n<div class=\"para\">\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>三、用手往下按榴莲的皮，有弹性就说明皮薄，就好，皮厚的按不动。</strong></span> \r\n</div>\r\n<div class=\"para\">\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>四、眼看榴莲尖尖，如果有细裂缝的最好，这是榴莲熟了的标志。如果榴莲是生的，就是用菜刀也砍不动。</strong></span> \r\n</div>\r\n<div class=\"para\">\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>五、选榴莲外形要圆，每一个鼓包凸出得多最好。</strong></span> \r\n</div>\r\n<div class=\"para\">\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>六、用鼻子嗅一下，熟了的榴莲嗅起来很香，（吃得来的是香，吃不来的嗅起来是臭）。如果嗅起来有酒的味道那就是熟过头了，这种不好吃。</strong></span> \r\n</div>\r\n<div class=\"para\">\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>七、榴莲属于热带水果</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>，营养非常丰富，很容易上火。也容易让人发胖。减肥的人最好别吃，也最好晚上9点以后也不要吃榴莲。</strong></span> \r\n</div>\r\n<div class=\"para\">\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>八、开榴莲的方法，用菜刀尖部朝榴莲的尖尖开裂缝的地方轻轻一瓣就开了，一次不要全取出，吃多少就开多少，一芽一芽的开，放在外面不会坏，但是最好用保鲜膜包好，因为很招一种小</strong></span><span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>虫来盯。不要放冰箱，因为味太重。（飞机上都标明不准带榴莲上机）。</strong></span> \r\n</div>\r\n<div class=\"para\">\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>九、若瓣发黄，榴莲可能已经烂了。</strong></span> \r\n</div>\r\n<p>\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong><br />\r\n</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;color:#E53333;line-height:1.5;\"><strong>小窍门：</strong></span> \r\n</p>\r\n<div class=\"para\">\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>1.未开口的榴莲，不成熟的有一股青草味，成熟的散发出榴莲固有的香气。当购买未成熟的榴莲，回家用报纸包住，点燃报纸，待燃完后再另用报纸包好，放在温暖处，一两天后能闻到香味证明已经成熟，想吃时提起来在地上轻摔，摔出裂口，从袭口处撬开即可食用。</strong></span> \r\n</div>\r\n<div class=\"para\">\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>2.榴莲食用完后手上会有一股臭味，该怎么办呢？据说用榴莲外壳装水清洗，就可去除。</strong></span> \r\n</div>\r\n<div class=\"para\">\r\n	<span style=\"font-size:16px;color:#009900;line-height:1.5;\"><strong>3.初学吃者买榴莲以七八成熟为佳，吃起来臭味不很重，较易接受。</strong></span> \r\n</div>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<img src=\"http://www.buyfer.com/upfiles/attached/image/20130615/20130615083717_98508.jpg\" alt=\"\" /> \r\n</p>', '', '40', '0', '1,4', '1', '1', '榴莲', '榴莲');
INSERT INTO `ot_goods` VALUES ('222', '2013-06-15 16:47:43', '2013-06-19 19:42:02', 'goods', '49', '0', '木耳菜', '/500g', '3.38', '1119', '450-500g', '201306151648586735.jpg', 'a:1:{i:0;s:22:\"201306151649223306.jpg\";}', '', '3106', '<p>\r\n	<span style=\"font-family:NSimSun;font-size:18px;color:#E53333;\"><strong><span style=\"line-height:2;\">商品详情</span><img src=\"/upfiles/attached/image/20130615/20130615085602_72979.jpg\" width=\"500\" height=\"375\" align=\"right\" alt=\"\" /></strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:NSimSun;color:#009900;line-height:2;\"><strong>品名：木耳菜</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;font-family:NSimSun;color:#009900;line-height:2;\"><strong>属性：</strong></span><span style=\"font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;color:#009900;\"><strong>木耳菜别名</strong></span><span style=\"font-size:14px;font-family:NSimSun;color:#009900;line-height:2;\"><strong>落葵</strong></span><span style=\"font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;color:#009900;\"><strong>、藤菜、</strong></span><span style=\"font-size:14px;font-family:NSimSun;color:#009900;line-height:2;\"><strong>承露</strong></span><span style=\"font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;color:#009900;\"><strong>、无葵、</strong></span><span style=\"font-size:14px;font-family:NSimSun;color:#009900;line-height:2;\"><strong>软浆叶</strong></span><span style=\"font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;color:#009900;\"><strong>、篱笆菜、御菜、紫豆菜、</strong></span><span style=\"font-size:14px;font-family:NSimSun;color:#009900;line-height:2;\"><strong>胭脂菜</strong></span><span style=\"font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;color:#009900;\"><strong>、</strong></span><span style=\"font-size:14px;font-family:NSimSun;color:#009900;line-height:2;\"><strong>豆腐菜</strong></span><span style=\"font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;color:#009900;\"><strong>、染浆叶、</strong></span><span style=\"font-size:14px;font-family:NSimSun;color:#009900;line-height:2;\"><strong>红果儿</strong></span><span style=\"font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;color:#009900;\"><strong>、</strong></span><span style=\"font-size:14px;font-family:NSimSun;color:#009900;line-height:2;\"><strong>繁露</strong></span><span style=\"font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;color:#009900;\"><strong>、软姜子等，属</strong></span><span style=\"font-size:14px;font-family:NSimSun;color:#009900;line-height:2;\"><strong>落葵科</strong></span><span style=\"font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;color:#009900;\"><strong>一年生蔓生</strong></span><span style=\"font-size:14px;font-family:NSimSun;color:#009900;line-height:2;\"><strong>草本植物。</strong></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:18px;color:#E53333;line-height:2;\"><strong>营养价值</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;color:#009900;\"><strong>木耳菜不但美味而且营养素含量极其丰富，尤其钙、铁等元素含量甚高，除蛋白质含量比苋菜稍少之外，其他项目与苋菜不相上下其中因富含维生素A、维生素C、B族维生素和蛋白质，而且热量低、脂肪少，木耳菜的钙含量很高，且草酸含量极低，是补钙的优选经济菜。</strong></span><strong><span style=\"color:#009900;font-size:14px;font-family:NSimSun;line-height:2;\"></span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:14px;line-height:2;background-color:#FFFFFF;color:#009900;\"><strong><br />\r\n</strong></span> \r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#E53333;font-family:NSimSun;font-size:18px;line-height:2;\">食疗作用</span></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#009900;line-height:2;\">1、</span></strong><span style=\"font-family:arial, 宋体, sans-serif;font-size:14px;line-height:2;background-color:#FFFFFF;color:#009900;\"><strong>降血压、益肝、清热凉血、利尿、防止便秘</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\"><strong><span style=\"color:#009900;line-height:2;\">2、</span></strong><span style=\"font-family:arial, 宋体, sans-serif;font-size:14px;line-height:2;background-color:#FFFFFF;color:#009900;\"><strong>抗癌防癌</strong></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\"><span style=\"font-family:arial, 宋体, sans-serif;font-size:14px;line-height:2;background-color:#FFFFFF;color:#009900;\"><strong><br />\r\n</strong></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:NSimSun;font-size:18px;line-height:24px;background-color:#FFFFFF;color:#E53333;\"><span style=\"font-family:NSimSun;font-size:18px;line-height:2;background-color:#FFFFFF;color:#E53333;\"><strong>适宜人群</strong></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:arial, 宋体, sans-serif;font-size:14px;line-height:24px;background-color:#FFFFFF;\"><span style=\"font-family:arial, 宋体, sans-serif;font-size:14px;line-height:2;background-color:#FFFFFF;color:#009900;\"><strong> </strong></span></span> \r\n</p>\r\n<div class=\"para\" style=\"font-family:arial, 宋体, sans-serif;font-size:14px;background-color:#FFFFFF;\">\r\n	<strong><span style=\"color:#E53333;\">【宜】</span><span style=\"color:#009900;\">高血压、肝病、便秘患者可以多食，极适宜老年人食用。</span></strong> \r\n</div>\r\n<p>\r\n	<strong><span style=\"color:#E53333;font-size:14px;\">【忌】</span><span style=\"font-size:14px;color:#009900;\">孕妇及脾胃虚寒者慎食。</span><strong></strong> </strong> \r\n</p>\r\n<p>\r\n	<strong><strong><br />\r\n</strong> </strong> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;color:#E53333;\"><strong>食谱推荐</strong></span> \r\n</p>\r\n<p>\r\n	<span style=\"font-family:arial, 宋体, sans-serif;font-size:14px;line-height:2;background-color:#FFFFFF;\"></span><span style=\"background-color:#FFFFFF;font-family:Arial;font-size:1