INSERT INTO `ot_infoweb` VALUES ('11', 'SinglePage', '新手入门', '', '<p>\r\n	<b><span style=\"font-size:16px;\">第</span><span style=\"font-size:16px;\"></span><span style=\"font-size:16px;\">三步：綁定個人信息1</span></b>\r\n</p>\r\n<p>\r\n	<span style=\"font-size:16px;\">為了确保您的帳號及財產安全，請及時綁定個人信息，并及時設置安全郵箱與安全密保問題，以便忘記密碼能夠自行取回。</span> \r\n</p>\r\n<p>\r\n	<b><span style=\"font-size:16px;\">第四步：忘記密碼如何找回</span></b><br />\r\n<span style=\"font-size:16px;\"> 如果您已按上面第三步設置了密保問題可通過首頁的找回密碼功能自行快速找回。</span><br />\r\n<b><span style=\"font-size:16px;\">第五步：我要註冊更多游戲帳號，要怎麼操作？</span></b><br />\r\n<span style=\"font-size:16px;\"> 1、點擊上面的“註冊帳號” 2、點擊上面的“</span><span style=\"font-size:16px;\">帳號</span><span style=\"font-size:16px;\">管理”--“激活帳號”激活即可</span><br />\r\n<b><span style=\"font-size:16px;\">第六步：我要金幣怎麼獲得？</span></b><br />\r\n<span style=\"font-size:16px;\"> 1、每個帳號每天可以獲得不同數量金幣。</span><br />\r\n<span style=\"font-size:16px;\"> 2、通過上面的“交易系統”---“搶購金幣”進行搶購，當有記錄時及時搶購來獲得。</span><br />\r\n<span style=\"font-size:16px;\"> 3、通過線下聯系身邊認識的有金幣的玩家或咨詢自己的推荐人或團隊領導人收集金幣。 </span><br />\r\n<b><span style=\"font-size:16px;\">第七步：我要怎麼查看我的收入等情況</span></b><br />\r\n<span style=\"font-size:16px;\"> 點擊首頁查看會員帳戶來了解簡要收入情況，另外可點擊上面“財務中心”---“游戲獎金”以及“金幣明細”來了解收入及金幣明細 </span><br />\r\n<b><span style=\"font-size:16px;\">第八步：了解平臺更多信息</span></b><br />\r\n<span style=\"font-size:16px;\"> 玩家要了解平臺更多信息可以點擊系統公告以及幫助中心查看相關文章 </span><br />\r\n<b><span style=\"font-size:16px;\">第九步：如何聯系平臺</span></b><br />\r\n<span style=\"font-size:16px;\"> 玩家如有相關疑問，在平臺又未找到相關答案，那可點擊上面的聯絡我們給我們提交留言，平臺的客服人員將盡快給您解答，</span><br />\r\n<span style=\"font-size:16px;\"> 通過留言系統提交記錄方便公司進行統一管理，更好的處理各類問題。 </span><br />\r\n<span style=\"font-size:16px;\"> 其它更多功能，玩家可以通過點擊系統中的相關內容進行詳細查看！&nbsp;</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '20', '', '', '', '');
