INSERT INTO `ot_single` VALUES ('1', 'help', '1', '6', '<p>\r\n	<strong><span style=\"line-height:2;\">如何注册</span></strong> \r\n</p>\r\n<p>\r\n	<img src=\"/upfiles/attached/image/20130610/20130610034052_79790.jpg\" alt=\"\" /> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:2;font-size:14px;font-family:NSimSun;\">1、点击页面顶部“免费注册“,进入注册页面；</span> \r\n</p>\r\n<div style=\"margin:0px;padding:0px;\">\r\n	<img src=\"/upfiles/attached/image/20130610/20130610034258_39616.jpg\" alt=\"\" /><br />\r\n</div>\r\n<p>\r\n	<span style=\"line-height:2;font-family:NSimSun;\">2、填写账户名、验证邮箱、密码、确认密码、验证码等个人信息进行注册；</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:2;font-family:NSimSun;\">3、选中“我已阅读并同意《百惠臻品用户注册协议》提交”，完成注册。请在注册时务必详细填写个人信息；</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:2;font-family:NSimSun;\">4、对于忘记密码，我们提供了找回密码的功能，请在忘记密码的页面中输入您的账户名和注册时填写的电子邮箱，</span> \r\n</p>\r\n<p>\r\n	<span style=\"line-height:2;font-family:NSimSun;\">系统将发送找回密码的链接到您注册</span><span style=\"font-family:NSimSun;line-height:2;\">的邮箱里：或者联系客服，客服会帮你还原为初始密码。</span>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<br />\r\n<span style=\"color:#333333;font-family:Arial, Verdana, 宋体;background-color:#FFFFFF;\"></span> \r\n<h3 class=\"help_tit\" style=\"font-size:14px;background-color:#FFFFFF;color:#333333;font-family:Arial, Verdana, 宋体;\">\r\n	<br class=\"Apple-interchange-newline\" />\r\n</h3>\r\n<p>\r\n	<br />\r\n</p>');
