  `MA_subIP` varchar(50) DEFAULT NULL COMMENT '信息提交IP',
  `MA_otherInfo` text DEFAULT NULL COMMENT '其它信息',
  `MA_note` text DEFAULT NULL COMMENT '用户留言内容',
  `MA_reply` text DEFAULT NULL COMMENT '管理员回复内容',
  `MA_status` smallint(1) DEFAULT 0 COMMENT '审核状态',
  `zt` int(8) NOT NULL DEFAULT 0,
  `pic` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`MA_ID`),
  KEY `MA_dataID` (`MA_dataID`),
